```javascript
function() {
    var o = new MutationObserver(function() {
        var n = document.querySelector(".ch2-open-settings-btn");
        n && setTimeout(function() {
            n.click(), o.disconnect(), setTimeout(function() {
                var n = document.querySelector('.ch2-settings-option > div.ch2-switch > input[name="c-3"]');
                n && (n.click(), setTimeout(function() {
                    var n = document.querySelector('.ch2-settings-actions > button.ch2-save-settings-btn');
                    n && n.click()
                }, 300))
            }, 300)
        }, 300)
    });
    o.observe(document, {
        childList: !0,
        subtree: !0
    }), setTimeout(function() {
        o.disconnect()
    }, 1e4)
};
```

