(function () {
  const clickEl = function () {
    var hostElement = document.getElementById('usercentrics-root');
    if (hostElement && hostElement.shadowRoot) {
      var shadowElement = hostElement.shadowRoot.querySelector('button[data-testid="uc-accept-all-button"]');
      if (!shadowElement) return;
      clearInterval(interval);
      shadowElement.click();
    }
  };
  const interval = setInterval(clickEl, 100);
  setTimeout(function () {
    clearInterval(interval);
  }, 1E4);
})();
