outlook.live.com#%#AG_onLoad((function(){new MutationObserver((function(){const t=document.querySelectorAll('main div[style^="position: absolute; left:"][style*="display: none"] ~ div[style*="top"]');t.length&&t.forEach(((t,e)=>{t.dataset.top||(t.style.top=`${parseInt(t.style.top)-80}px`,t.dataset.top=!0)}))})).observe(document,{childList:!0,subtree:!0,attributes:!0})}));
